<?php
function wp_room012_add_submenu_page()
{
	$theme_page = add_theme_page(
		__( 'Theme Options', 'wp-bootstrap' ),
		__( 'Theme Options', 'wp-bootstrap' ),
		'edit_theme_options',
		'theme_options',
		'wp_room012_main_page'
	);
	add_action( "admin_print_styles-{$theme_page}", 'wp_room012_admin_enqueue_scripts' );
}

add_action( 'admin_menu', 'wp_room012_add_submenu_page' );


function wp_room012_main_page()
{
?>
<div class="wrap">

    <?php screen_icon(); ?>
    <h2><?php _e( 'Wp Bootstrap Customization', 'wpbootstrap' ); ?></h2>
    <?php settings_errors(); ?>

    <div id="poststuff">
        <div id="post-body" class="obenland-wp columns-2">
            <div id="post-body-content">
            <?php
            if(isset($_POST['act']) == 'send')
            {
                update_option('wp_room012_responsive', $_POST['wp_room012_responsive']);
                update_option('wp_room012_fluid', $_POST['wp_room012_fluid']);
                update_option('wp_room012_show_top_bar', $_POST['wp_room012_show_top_bar']);
                update_option('wp_room012_show_social', $_POST['wp_room012_show_social']);
            }
            ?>
                <style type="text/css">
                    .wpbootstrap_settings{ margin:10px; padding:0; float:left; }
                    .wpbootstrap_settings li{ width:150px; height:70px; float:left; }
                    .wpbootstrap_settings h2{ margin:0; padding:0; }
                </style>             
            <form method="post">
                    <ul class="wpbootstrap_settings">
                        <li>
                            <h2><?php echo _e('Make responsive'); ?></h2>
                            <p>
                                <?php echo _e('No'); ?> <input type="radio" name="wp_room012_responsive" value="0" <?php echo (get_option('wp_room012_responsive') == 0) ? 'checked' : false; ?> />
                                <?php echo _e('Yes'); ?> <input type="radio" name="wp_room012_responsive" value="1" <?php echo (get_option('wp_room012_responsive') == 1) ? 'checked' : false; ?> />
                            </p>
                        </li>

                        <li>
                            <h2><?php echo _e('Make fluid'); ?></h2>
                            <?php echo _e('No'); ?> <input type="radio" name="wp_room012_fluid" value="0" <?php echo (get_option('wp_room012_fluid') == 0) ? 'checked' : false; ?> />
                            <?php echo _e('Yes'); ?> <input type="radio" name="wp_room012_fluid" value="1" <?php echo (get_option('wp_room012_fluid') == 1) ? 'checked' : false; ?> />
                        </li>

                        <li>
                            <h2><?php echo _e('Display top bar'); ?></h2>
                            <?php echo _e('No'); ?> <input type="radio" name="wp_room012_show_top_bar" value="0" <?php echo (get_option('wp_room012_show_top_bar') == 0) ? 'checked' : false; ?> />
                            <?php echo _e('Yes'); ?> <input type="radio" name="wp_room012_show_top_bar" value="1" <?php echo (get_option('wp_room012_show_top_bar') == 1) ? 'checked' : false; ?> />
                        </li>
                        
                        <li>
                            <h2><?php echo _e('Display social network share'); ?></h2>
                            <?php echo _e('No'); ?> <input type="radio" name="wp_room012_show_social" value="0" <?php echo (get_option('wp_room012_show_social') == 0) ? 'checked' : false; ?> />
                            <?php echo _e('Yes'); ?> <input type="radio" name="wp_room012_show_social" value="1" <?php echo (get_option('wp_room012_show_social') == 1) ? 'checked' : false; ?> />
                        </li>
                    </ul>

                    <input type="hidden" name="act" value="send" />
                    <input type="submit" class="button-primary" name="send" value="<?php echo _e('Update'); ?>" />
            </form>
            </div>
            
            <div id="postbox-container-1">
                    <div id="side-info-column">
                            <?php do_action( 'wp_room012_show_more' ); ?>
                    </div>
            </div>
            </div>
    </div>
</div>
<?php
}


